import React from 'react';
import Img from 'react-image'


const Container = (props) => {


    return (

        <div id="main-container">
            {props.children}
            <Img src={props.img} 
                 loader={<div className="loading">LOADING</div>} decode={false} />
            <style jsx global>{`

        html {
            width: 100%;
            height: 100%;
            background: url(${props.img}) no-repeat center center fixed; 
            -webkit-background-size: cover;
            -moz-background-size: cover;
            -o-background-size: cover;
            background-size: ${props.infoToggle ? '100%' : '70%'};
            position: relative; 
            background-position: ${props.infoToggle ? '0 -250px' : '-100px 0'};
            transition: all 0.2s;
            overflow: hidden;
        }
        img{opacity: 0;}
        .loading {font-size: 150px;}




   @media only screen and (max-width: 1024px) {

    html {
        width: 100%;
        height: 100%;
        background: url(${props.img}) no-repeat center center scroll; 
        -webkit-background-size: cover;
        -moz-background-size: cover;
        -o-background-size: cover;
        background-size: cover;
        position: relative; 
        background-position: ${props.infoToggle ? '0 0' : '-300px 0px'};
        transition: all 0.2s;
        overflow: hidden;
    }

   } 
           `}</style>
        </div>

    )
}

export default Container;
