import React from 'react';
import Img from 'react-image'


const Container = (props) => {


    return (

        <div id="main-container">
            {props.children}
            <div className="loading">LOADING</div>
            <Img src={props.img}
                className="animated fadeIn"
                loader={<div className="loading">..LOADING..</div>}
                decode={false} />

            <style jsx global>{`



        .loading { width: 100%; font-size: 40px; text-align: center; margin-top: 250px; }
        #main-container {width: 600px; margin: 0 auto; position:relative;}
        img { width: 100%;}


   @media only screen and (max-width: 1024px) {

    #main-container {width: 100%; margin: 0 auto; min-height: 800px; position:relative;}
    .loading { width: 100%; font-size: 40px; text-align: right; margin-top: 450px; }
   } 
           `}</style>
        </div>

    )
}

export default Container;
