import React from 'react';
import Img from 'react-image'


const Container = (props) => {


    return (

        <div id="main-container">
            {props.children}
            <div className="loading">LOADING</div>
            <Img src={props.img}
                className="animated fadeIn"
                loader={<div className="loading">LOADING</div>}
                decode={false} />

            <style jsx global>{`



        .loading {font-size: 150px; background: url(../public/static/spinner.gif) width: 50px; height:50px;}
        #main-container {width: 800px; margin: 0 auto; position:relative;}
        img { width: 100%;}


   @media only screen and (max-width: 1024px) {

    #main-container {width: 100%; margin: 0 auto; position:relative;}

   } 
           `}</style>
        </div>

    )
}

export default Container;
