import React from 'react';
import ImageLoading from 'react-image-lazy-progressive-load-with-progress-bar';

const Container = (props) => {


    return (



        <div>

            <ImageLoading
                preview="${props.img}"
                src={props.img}
                width={600}
                height={600}
                lazyLoadScreenOffSet={200}
                blur={10}
                transitionTime={0.6}
                spinnerSize={70}
            />

            <style jsx global>{`





   @media only screen and (max-width: 1024px) {

    html {
        width: 100%;
        height: 100%;
        background: url(${props.img}) no-repeat center center scroll; 
        -webkit-background-size: cover;
        -moz-background-size: cover;
        -o-background-size: cover;
        background-size: cover;
        position: relative; 
        background-position: ${props.infoToggle ? '0 0' : '-300px 0px'};
        transition: all 0.2s;
        overflow: hidden;
    }

   } 
           `}</style>
        </div>

    )
}

export default Container;
