import React, { Component } from 'react';

class Container extends Component {

    componentDidMount() {

        const img = new Image();
        img.src = this.props.img

        img.onload = () => {
            alert('loaded');
        };

    }



    render() {


        return (
            <div id="main-container">



                {this.props.children}

            </div>
        );
    }
}

export default Container;
