import React from 'react';
import Img from 'react-image'


const Container = (props) => {


    return (

        <div id="main-container">
            {props.children}

            <Img src={props.img}
                className="animated fadeIn"
                loader={<div className="loading animated fadeOut">LOADING</div>}
            />

            <style jsx global>{`

        html, body {
            height: 100%;
            width: 100%;
            background: #000;
        }

        #main-container { width: 700px; margin:0 auto; position:relative; overflow: hidden; }
        .loading {font-size: 150px;}
        img {width: 100%; min-height: 700px;}


   @media only screen and (max-width: 1024px) {

    #main-container { width: 100%; margin:0 auto;}
    img {width: 100%; min-height: 400px;}


   } 
           `}</style>
        </div>

    )
}

export default Container;
