import React, { Component } from 'react';

class Container extends Component {

    state = {
        loaded: false
    }



    componentDidMount() {

        const to = new Image();
        to.src = this.props.img

        to.onload = () => {
            this.setState({
                loaded: true,
                img: to
            })
        };
    }



    render() {

        const test = new Image();
        test.src = this.props.img

        test.onload = () => {
            this.setState({
                loaded: true
            })
        };

        return (
            <div id="main-container">
                {this.state.loaded ? test : 'loading'}
                {this.props.children}

            </div>
        );
    }
}

export default Container;
