import React, { Component } from 'react';

class Container extends Component {

    state = {
        loaded: false,
        img: null
    }



    componentDidMount() {

        const img = new Image();
        img.src = this.props.img

        img.onload = () => {
            this.setState({
                loaded: true,
                img: img
            })
        };
    }



    render() {


        return (
            <div id="main-container">
                {img}
                {this.props.children}

            </div>
        );
    }
}

export default Container;
